mapper.insert:

insert into orderInfo (orderNumber, orderStatus, orderPayment,
  orderPaymentStatus, orderPaymentTime,
  orderClientId, orderApplyInfoId, createTime,
  updateTime, createBy, isVisible
  )
values (#{ordernumber,jdbcType=VARCHAR}, #{orderstatus,jdbcType=VARCHAR}, #{orderpayment,jdbcType=DECIMAL},
  #{orderpaymentstatus,jdbcType=VARCHAR}, #{orderpaymenttime,jdbcType=TIMESTAMP},
  #{orderclientid,jdbcType=INTEGER}, #{orderapplyinfoid,jdbcType=INTEGER}, #{createtime,jdbcType=TIMESTAMP},
  #{updatetime,jdbcType=TIMESTAMP}, #{createby,jdbcType=VARCHAR}, #{isvisible,jdbcType=VARCHAR}
  )

mapper.select:
SELECT orderNumber,driverName,driverCarNumber,applyDate,createTime,orderPaymentStatus,orderPaymentTime,cardNumber,telphone,applyTime,
case WHEN driverCarType='1' THEN 20 WHEN driverCarType='2' THEN 30 WHEN driverCarType='3' THEN 40 ELSE null END as orderPayment FROM orderInfo INNER JOIN (SELECT applyInfo.ID,driverName,driverCarNumber,applyDate,applyTime,driverApplyType,driverId,cardNumber,driverCarType,telphone FROM applyInfo LEFT JOIN driverInfo on applyInfo.driverId=driverInfo.ID)as applyInfo ON applyInfo.ID=orderInfo.orderApplyInfoId where 1=1 and driverid=(SELECT id from driverInfo WHERE
cardNumber = #{cardnumber,jdbcType=VARCHAR}
LIMIT 1)
and orderPaymentStatus = #{orderpaymentstatus,jdbcType=VARCHAR}
and orderInfo.isVisible =1
ORDER BY createTime ${applyBy}
