mapper.insert:

insert into clientInfo (ID, clientUniqueId, clientPassword,
  clientName, clientPhone, clientLoginError,
  clientAllowedLoginTime, createTime,
  updateTime, isVisible, createBy
  )
values (#{id,jdbcType=INTEGER}, #{clientuniqueid,jdbcType=VARCHAR}, #{clientpassword,jdbcType=VARCHAR},
  #{clientname,jdbcType=VARCHAR}, #{clientphone,jdbcType=VARCHAR}, #{clientloginerror,jdbcType=INTEGER},
  #{clientallowedlogintime,jdbcType=TIMESTAMP}, #{createtime,jdbcType=TIMESTAMP},
  #{updatetime,jdbcType=TIMESTAMP}, #{isvisible,jdbcType=VARCHAR}, #{createby,jdbcType=VARCHAR}
  )

mapper.select:
select * from clientInfo where 1=1
and ID = #{id,jdbcType=INTEGER}
and isVisible = 1

select count(*)
from clientInfo
where clientUniqueId = #{clientuniqueid,jdbcType=VARCHAR} and clientPassword =#{clientpassword,jdbcType=VARCHAR}

SELECT applyInfo.ID,driverName,applyDate,driverCarNumber,orderClientId,applyInfo.createTime,telphone,driverCarType,driverApplyType,applyTime,driverApplyStatus
from orderInfo LEFT JOIN applyInfo on applyInfo.ID=orderInfo. orderApplyInfoId INNER JOIN (SELECT driverName,applyInfo.ID,applyInfo.createTime,telphone FROM applyInfo LEFT JOIN driverInfo on applyInfo.driverId=driverInfo.ID)as newInfo ON newInfo.ID=orderInfo.orderApplyInfoId WHERE orderClientId = (SELECT ID FROM clientInfo WHERE
clientuniqueid = #{clientuniqueid,jdbcType=VARCHAR}
limit 1)
and driverApplyStatus = #{driverapplystatus,jdbcType=VARCHAR}
and driverApplytype='1'
ORDER BY createTime ${applyBy}
