mapper.select:

select ID,driverName,driverIdNumber,driverPhone,driverCarNumber,applyDate,applyTime, driverApplyStatus, driverApplyType, driverCarType
from driverInfo
where ID = #{id,jdbcType=INTEGER}

select *
from driverInfo
where driverIdNumber = #{driveridnumber,jdbcType=VARCHAR}

  and  driverOrderStatus=#{driverorderstatus,jdbcType=VARCHAR}


  and  driverOrderStatus=#{driverorderstatus,jdbcType=VARCHAR}


  order by applyTime ${orderBy}

select ID,driverName,driverIdNumber,driverPhone,driverCarNumber,applyDate,isVisible, case WHEN driverApplyStatus='0' THEN "已提交申请" WHEN driverApplyStatus='1' THEN "审核中" WHEN driverApplyStatus='2' THEN "审核完成" WHEN driverApplyStatus='-1' THEN "申请无效" ELSE "其他" END as driverApplyStatus, case when driverApplyType='1' then "已入园" when driverApplyType='2' then "已出园" else "其他" end as driverApplyType, case WHEN driverCarType='1' THEN "卡车" WHEN driverCarType='2' THEN "货车" WHEN driverCarType='3' THEN "汽车" ELSE "其他" END as driverCarType, case WHEN applyTime='1' THEN "6:00-9:00" WHEN applyTime='2' THEN "9:00-12:00" WHEN applyTime='3' THEN "12:00-15:00" WHEN applyTime='4' THEN "15:00-18:00" ELSE "null" end as applyTime from driverInfo where 1=1
and ID=#{id,jdbcType=INTEGER}
and isVisible =1 order by createTime desc
